const callToApi = () => {
    // Recibimos por par치metros searchName, que nos lo pasan desde App dentro del useEffect
    // Llamamos al fetch interpolando la URL del API m치s el nombre buscado
    return fetch('https://beta.adalab.es/pw-recursos/apis/adalabers-v1/promo-patata.json') 
      .then(response => response.json())
      .then(response => {
       //Limpieza de datos

        const results = response.results
        return results;
      });
  };

  export default callToApi;
